function addEvent(b,a,c){if(b.addEventListener){b.addEventListener(a,c,false)}else{if(b.attachEvent){b.attachEvent("on"+a,c)}}}function getEle(a){var b=a.charAt(0)==="."?"getElementsByClassName":"getElementById";return document[b](a.substr(1))}function func(){var g=getEle("#wrapper");var c=getEle(".bottom")[0];var e=getEle(".top")[0];var d=getEle(".handle")[0];var a=g.className;var b=1000;var f=0;addEvent(g,"mousemove",function(h){var i=h||window.event;f=(i.clientX-window.innerWidth/2)*0.5;d.style.left=i.clientX+f+"px";e.style.width=i.clientX+b+f+"px"})}addEvent(document,"DOMContentLoaded",func);